mapper.select:
SELECT t.* FROM JY_PUSH_DEVICE t WHERE 1=1 

 	AND t.id=#{id}


 	AND t.deviceId=#{deviceId}


 	AND t.pushToken=#{pushToken}

SELECT t.* FROM JY_PUSH_DEVICE t WHERE 1=1 

 	AND t.id=#{param.id}


 	AND t.isValid=#{param.isValid}


 	AND (
 	t.deviceId LIKE CONCAT('%',#{param.keyWord},'%')    
 	OR   t.deviceId LIKE CONCAT('%',#{param.keyWord},'%')
 	OR   t.deviceId LIKE CONCAT('%',#{param.keyWord},'%')
 	)

order by t.createTime desc

mapper.delete:
DELETE FROM JY_PUSH_DEVICE 
WHERE id IN(

		#{o.id}

	)
